# -*- coding: utf-8 -*-
from typing import *
from operator import itemgetter

class Solution:
    def removeCoveredIntervals(self, intervals: List[List[int]]) -> int:
        if len(intervals) == 1:
            return 1

        intervals.sort(key=lambda x:(x[0], -x[1]))
        # print(intervals)

        end_val = 0
        ret = 0
        for first, end in intervals:
            if end_val < end:
                end_val = end
                ret += 1

        # print(ret)
        return ret

